(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[325],{a96e:function(e,n,a){"use strict";a.r(n),n["default"]='<template>\n  <div style="max-width: 800px; width: 100%;">\n    <q-calendar\n      v-model="selectedDate"\n      view="week"\n      :disabled-days="disabledDays"\n      locale="en-us"\n      style="height: 400px;"\n    />\n  </div>\n</template>\n\n<script>\n// normally you would not import "all" of QCalendar, but is needed for this example to work with UMD (codepen)\nimport QCalendar from \'ui\' // ui is aliased from \'@quasar/quasar-ui-qcalendar\'\n\nconst CURRENT_DAY = new Date()\n\nfunction getCurrentDay (day) {\n  const newDay = new Date(CURRENT_DAY)\n  newDay.setDate(day)\n  const tm = QCalendar.parseDate(newDay)\n  return tm.date\n}\n\nexport default {\n  data () {\n    return {\n      selectedDate: getCurrentDay(CURRENT_DAY.getDate())\n    }\n  },\n  computed: {\n    disabledDays () {\n      const days = []\n      // get current day\n      const ts = QCalendar.parseTimestamp(this.selectedDate)\n      // make next 4 days, after today, disabled\n      Array.from(Array(4)).forEach((_, i) => {\n        days.push(QCalendar.addToDate(ts, { day: i + 1 }).date)\n      })\n      return days\n    }\n  }\n}\n<\/script>\n'}}]);